function init() {
	// 计算高度
	(function (h) {
		// 主体高度
		$(".app_body").height(h);
		$("#app_body").css("min-height", h);
		// 快捷文本高度
		$(".app_body .body_right .right_li").css({
			height: h / 27 - 0.5,
			"line-height": (h / 27 - 0.5) + "px"
		});
	})(document.documentElement.clientHeight - 75);

	// 自定义滚动条
	$scroll.init("#app_body", function() {
	});

	// 缓存选择
	$(".djxz").mousedown(function() {
		sessionStorage[$("#colname").val() + "_text"] = $(this).attr("text");
		sessionStorage[$("#colname").val() + "_value"] = $(this).attr("value");
		if ({ 
			1: "BrandName", 
			2: "SeriesName",
			3: "Modelname" 
		}[$("#type").val()] == $("#colname").val()) {
			// 是否填充
			sessionStorage.isfill = true;
			// 返回查询页
			location.href = sessionStorage.href
				.setQueStr("choose_year", sessionStorage.choose_year || '')
				.setQueStr("choose_city", sessionStorage.choose_city || '')
				.setQueStr("choose_interval", sessionStorage.choose_interval || '')
				.setQueStr("choose_brand", sessionStorage.BrandName_value || '')
				.setQueStr("choose_series", sessionStorage.SeriesName_value || '')
				.setQueStr("pageindex", 1);
		}
	});

	// 不选清空
	$(".qk").click(function() {
		delete sessionStorage.BrandName_text;
		delete sessionStorage.BrandName_value;
		delete sessionStorage.SeriesName_text;
		delete sessionStorage.SeriesName_value;
		location.href = sessionStorage.href
			.setQueStr("choose_year", sessionStorage.choose_year || '')
			.setQueStr("choose_city", sessionStorage.choose_city || '')
			.setQueStr("choose_interval", sessionStorage.choose_interval || '')
			.setQueStr("choose_brand", sessionStorage.BrandName_value || '')
			.setQueStr("choose_series", sessionStorage.SeriesName_value || '')
			.setQueStr("pageindex", 1);
	});
	
	// 不限
	$(".bx").click(function() {
		// 是否填充
		sessionStorage.isfill = true;
		delete sessionStorage.SeriesName_text;
		delete sessionStorage.SeriesName_value;
		location.href = sessionStorage.href
			.setQueStr("choose_year", sessionStorage.choose_year || '')
			.setQueStr("choose_city", sessionStorage.choose_city || '')
			.setQueStr("choose_interval", sessionStorage.choose_interval || '')
			.setQueStr("choose_brand", sessionStorage.BrandName_value || '')
			.setQueStr("choose_series", sessionStorage.SeriesName_value || '')
			.setQueStr("pageindex", 1);
	});
}