function addRelationsApply(id) {
	// 添加好友
    $.ajax({
        url: "/api/addRelationsApply",
        type: "post",
        data: {
            innerid: id,
            remark: ""
        },
        success: function(msg) {
        	sessionStorage.isalert = true;
        	window.location.href = setQueStr(sessionStorage.href, "isalert", "true");
        }
    });
}

$(".app").scroll(function() {
	$(".app_bottom").css("top", document.documentElement.clientHeight + $(".app").scrollTop() - 85);
});

// 手指事件,开始
$(".app_body").bind("touchstart", function() {
	$(".app_bottom").hide();
});
// 手指事件,离开
$(".app").bind("touchmove", function() {
	$(".app_bottom").hide();
}).bind("touchend", function() {
	setTimeout(function() {
		$(".app_bottom").show();
	}, 100);	
}).bind("touchcancel", function() {
	setTimeout(function() {
		$(".app_bottom").show();
	}, 100);	
});

function init() {
	// 计算高度
	$(".app_body").height(document.documentElement.clientHeight - ($("#isfriend").val() != "-1" && $("#ishide").val() != 'true') ? 85 : 0 );
	$(".app_bottom").css("top", document.documentElement.clientHeight - 85);

	// 控制弹出alert
	if (getQueryString("isalert") == "true") {
		// 禁止滚动
		$("body").css("overflow", "hidden");
		$(".app_alert").show();
	}

	// 新车指导信息获取
	$.ajax({
        url: "/api/carEvaluateById",
        type: "get",
        data: {
            carid: $("#Innerid").val()
        },
        success: function(msg) {
        	if (parseInt(msg.val)) {
        		$(".body_header .block_1_hq .zdj span").html(msg.val);
        	} else {
	            $(".body_header .block_1_hq .zdj").hide();
	        }
        }
    });

    // 每月成交量
    $.ajax({
        url: "/api/carSales",
        type: "get",
        data: {
            modelid: $("#model_id").val()
        },
        success: function(msg) {
        	if (parseInt(msg.val)) {
        		$(".body_header .block_1_hq .cjl span").html(msg.val);
        	} else {
	            $(".body_header .block_1_hq .cjl").hide();
	        }
        }
    });

	// 初始化滚动图片
	var swiper = new Swiper('.swiper-container', {
		pagination: '.swiper-pagination'
	});
}